import React, { useState } from 'react';
import '../../App.css';
import '../css/HomeStyle.css';
import { Link } from 'react-router-dom';

function Article({ i }) {
  const [isExpanded, setIsExpanded] = useState(false);

  const handleToggle = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <div className="article" key={i}>
      <h3>Projekt{i}</h3>
      <img src="" alt="Upsss"></img>
      <button onClick={handleToggle}>Rozwiń/Zwiń opis</button>
      {isExpanded && <p>Opis</p>}
      <Link to="/marketing"><button>Przejdź do Marketing</button></Link>
    </div>
  );
}

export default function Home() {
  return (
    <div className="home">
      <div className="About-me-short">
        <h1>O Mnie</h1>
      </div>
      <div className="container">
        <h2>Moje Projekty</h2>
        {[1,2,3,4,5,6].map((i) => (
          <Article i={i} />
        ))}
      </div>
    </div>
  );
}
